                <div class="image-wrapper"><div class="image_style" style="width:81%;"><picture><source sizes="(min-resolution: 4dppx) and (max-width: 700px) 50vw, (-webkit-min-device-pixel-ratio: 4) and (max-width: 700px) 50vw, (min-resolution: 3dppx) and (max-width: 700px) 67vw, (-webkit-min-device-pixel-ratio: 3) and (max-width: 700px) 65vw, (min-resolution: 2.5dppx) and (max-width: 700px) 80vw, (-webkit-min-device-pixel-ratio: 2.5) and (max-width: 700px) 80vw, (min-resolution: 2dppx) and (max-width: 700px) 100vw, (-webkit-min-device-pixel-ratio: 2) and (max-width: 700px) 100vw, 600px" srcset="https://miro.medium.com/v2/resize:fit:640/format:webp/1*WPmt6b5SMX0sr8QXeWnOog.png 640w, https://miro.medium.com/v2/resize:fit:720/format:webp/1*WPmt6b5SMX0sr8QXeWnOog.png 720w, https://miro.medium.com/v2/resize:fit:750/format:webp/1*WPmt6b5SMX0sr8QXeWnOog.png 750w, https://miro.medium.com/v2/resize:fit:786/format:webp/1*WPmt6b5SMX0sr8QXeWnOog.png 786w, https://miro.medium.com/v2/resize:fit:828/format:webp/1*WPmt6b5SMX0sr8QXeWnOog.png 828w, https://miro.medium.com/v2/resize:fit:1100/format:webp/1*WPmt6b5SMX0sr8QXeWnOog.png 1100w, https://miro.medium.com/v2/resize:fit:1200/format:webp/1*WPmt6b5SMX0sr8QXeWnOog.png 1200w" type="image/webp"/><source data-testid="og" sizes="(min-resolution: 4dppx) and (max-width: 700px) 50vw, (-webkit-min-device-pixel-ratio: 4) and (max-width: 700px) 50vw, (min-resolution: 3dppx) and (max-width: 700px) 67vw, (-webkit-min-device-pixel-ratio: 3) and (max-width: 700px) 65vw, (min-resolution: 2.5dppx) and (max-width: 700px) 80vw, (-webkit-min-device-pixel-ratio: 2.5) and (max-width: 700px) 80vw, (min-resolution: 2dppx) and (max-width: 700px) 100vw, (-webkit-min-device-pixel-ratio: 2) and (max-width: 700px) 100vw, 600px" srcset="https://miro.medium.com/v2/resize:fit:640/1*WPmt6b5SMX0sr8QXeWnOog.png 640w, https://miro.medium.com/v2/resize:fit:720/1*WPmt6b5SMX0sr8QXeWnOog.png 720w, https://miro.medium.com/v2/resize:fit:750/1*WPmt6b5SMX0sr8QXeWnOog.png 